import React, { useState, useEffect } from "react";
import axios from "axios";


const Customers = () => {
	const customer = {
		id: "",
		name: "",
		email: "",
	};
  	// const [customerDetails, setCustomerDetails] = useState([customer]);
	let customerDetails = [];
  	const [filtered, setFiltered] = useState([customer]);
  	const [ paginationNb, setPaginationNb] = useState(20);
	// console.log(customerDetails)
	// let filtered =  customerDetails;


	const filterBy = (e) => {
		if (Number.isInteger(parseInt(e.target.value))) {
			setFiltered(customerDetails.filter((customer) => 
				 customer.id === parseInt(e.target.value)
			));
		} else if (e.target.value.includes("@")) {
			setFiltered(customerDetails.filter((customer) => 
			 	 customer.email.includes(e.target.value)
			));
		} else {
			setFiltered(customerDetails.filter((customer) => 
				 customer.name.includes(e.target.value)
			));
		}
  	}

	const changeNbPerPage = (e) => {
		// console.log(e.target.value);
		setPaginationNb(parseInt(e.target.value)); 
  	};


	// useEffect(() => {
    // 	fetchData();
  	// }, [customerDetails]);


	useEffect(() => {
		async function fetchData() {
      		const response = await axios.post("http://localhost:8080/customers", {
        		paginationNb: paginationNb,
      		});
      		setFiltered(response.data);
			customerDetails = response.data;
      		console.log("customer = " , customerDetails)
    	}
		fetchData();
	}, [paginationNb]);


   

  	return (
      <section className="text-gray-600 body-font">
        <div className="p-4  mt-4 flex justify-between">
          <div className=" ">
            <label htmlFor="filter" className="leading-7 text-gray-700">
              Filter By
            </label>
            <input
              type="text"
              id="filter"
              name="filter"
              onChange={filterBy}
              className="w-full bg-gray-100 bg-opacity-50 rounded border border-gray-300 focus:border-indigo-500 focus:bg-white focus:ring-2 focus:ring-indigo-200 text-base outline-none text-gray-700 py-1 px-3 leading-8 transition-colors duration-200 ease-in-out"
            />
          </div>

          <div className=" ">
            <label htmlFor="search" className="leading-7 text-gray-700">
              number of customers
            </label>
            <select
              id="paginationNb"
              value={paginationNb}
              onChange={changeNbPerPage}
              className="w-full bg-gray-100 bg-opacity-50 rounded border
							border-gray-300 focus:border-indigo-500 focus:bg-white focus:ring-2
							focus:ring-indigo-200 text-base outline-none text-gray-700 py-1 px-3
							leading-8 transition-colors duration-200 ease-in-out"
            >
              {" "}
              <option value="20">20</option>
              <option value="40">40</option>
              <option value="60">60</option>
            </select>
          </div>
        </div>
        <div className="container px-3 pb-8  flex flex-wrap">
          {filtered.length === 0 ? (
            <></>
          ) : (
            filtered.map((customer, index) => (
              <div
                key={customer.id}
                className="flex relative py-10  sm:items-center md:w-2/3 "
              >
                <div className="flex-shrink-0 w-6 h-6 rounded-full mt-10 sm:mt-0 inline-flex items-center justify-center bg-indigo-500 text-white relative z-10 title-font font-medium text-sm">
                  {customer.id}
                </div>
                <div className="flex-grow md:pl-8 pl-6 flex sm:items-center items-start flex-col sm:flex-row">
                  <div className="flex-shrink-0 w-20 h-20 bg-indigo-100 text-indigo-500 rounded-full inline-flex items-center justify-center">
                    {customer.photoUrl ? (
                      <image src={customer.photoUrl} />
                    ) : (
                      <svg
                        className="w-12 h-12 stroke-current text-white"
                        fill="currentColor"
                        viewBox="0 0 20 20"
                      >
                        <path
                          fillRule="evenodd"
                          d="M10 9a3 3 0 100-6 3 3 0 000 6zm-7 9a7 7 0 1114 0H3z"
                          clipRule="evenodd"
                        ></path>
                      </svg>
                    )}
                  </div>
                  <div className="flex-grow sm:pl-6 mt-6 sm:mt-0">
                    <h2 className="font-medium title-font text-gray-900 mb-1 text-xl">
                      {customer.name}
                    </h2>
                    <p className="leading-relaxed">{customer.email}</p>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
        <div className="flex mb-12 justify-center items-center ">
          <span className=" text-indigo-500 border-b-2 border-indigo-500 py-2 text-lg px-2 mr-2 ">
            1
          </span>
          <span className=" border-b-2 border-gray-300 py-2 text-lg px-2 mr-2   hover:text-indigo-500 hover:border-indigo-500">
            2
          </span>
          <span className=" border-b-2 border-gray-300 py-2 text-lg px-2 mr-2  hover:text-indigo-500 hover:border-indigo-500">
            3
          </span>
          <svg
            className="w-4 h-4 ml-12 fill-gray-200 hover:cursor-pointer "
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path d="M5 12h14"></path>
            <path d="M12 5l7 7-7 7"></path>
          </svg>
        </div>
      </section>
    );
};

export default Customers;
