import React, { useState, useEffect } from "react";
import axios from "axios";
import Customers from "./Customers";

const CustomerList = () => {
	const customer = {
		id: "",
		name: "",
		email: "",
	};
  	const [customers, setCustomers] = useState([customer]);
  	const [filtered, setFiltered] = useState([customer]);
  	const [ paginationNb, setPaginationNb] = useState(20);
    const [ loading, setLoading] = useState(true);
	// console.log(customers)
	// let filtered =  customers;


	const filterBy = (e) => {
		if (Number.isInteger(parseInt(e.target.value))) {
			setFiltered(customers.filter((customer) => 
				 customer.id === parseInt(e.target.value)
			));
		} else if (e.target.value.includes("@")) {
			setFiltered(customers.filter((customer) => 
			 	 customer.email.includes(e.target.value)
			));
		} else {
			setFiltered(customers.filter((customer) => 
				 customer.name.includes(e.target.value)
			));
		}
  	}

	const changeNbPerPage = (e) => {
		// console.log(e.target.value);
		setPaginationNb(parseInt(e.target.value)); 
  	};


	// useEffect(() => {
    // 	fetchData();
  	// }, [customers]);


	useEffect(() => {
		   async function fetchData() {
      		const response = await axios.post("http://localhost:8080/customers", {
        		paginationNb: paginationNb,
      		});
      		setCustomers(response.data);
      		setFiltered(response.data);
          setLoading(false);
      		// console.log("customer = " , customers)
    	}
		  fetchData();
	}, [paginationNb]);


   

  	return (
      <section className="text-gray-600 body-font">
        <div className="p-4  mt-4 flex justify-between">
          <div className=" ">
            <label htmlFor="filter" className="leading-7 text-gray-700">
              Filter By
            </label>
            <input
              type="text"
              id="filter"
              name="filter"
              onChange={filterBy}
              className="w-full bg-gray-100 bg-opacity-50 rounded border border-gray-300 focus:border-indigo-500 focus:bg-white focus:ring-2 focus:ring-indigo-200 text-base outline-none text-gray-700 py-1 px-3 leading-8 transition-colors duration-200 ease-in-out"
            />
          </div>

          <div className=" ">
            <label htmlFor="search" className="leading-7 text-gray-700">
              number of customers
            </label>
            <select
              id="paginationNb"
              value={paginationNb}
              onChange={changeNbPerPage}
              className="w-full bg-gray-100 bg-opacity-50 rounded border
							border-gray-300 focus:border-indigo-500 focus:bg-white focus:ring-2
							focus:ring-indigo-200 text-base outline-none text-gray-700 py-1 px-3
							leading-8 transition-colors duration-200 ease-in-out"
            >
              {" "}
              <option value="20">20</option>
              <option value="40">40</option>
              <option value="60">60</option>
            </select>
          </div>
        </div>
        <Customers cc={customers} ll={loading} />
        <div className="flex mb-12 justify-center items-center ">
          <span className=" text-indigo-500 border-b-2 border-indigo-500 py-2 text-lg px-2 mr-2 ">
            1
          </span>
          <span className=" border-b-2 border-gray-300 py-2 text-lg px-2 mr-2   hover:text-indigo-500 hover:border-indigo-500">
            2
          </span>
          <span className=" border-b-2 border-gray-300 py-2 text-lg px-2 mr-2  hover:text-indigo-500 hover:border-indigo-500">
            3
          </span>
          <svg
            className="w-4 h-4 ml-12 fill-gray-200 hover:cursor-pointer "
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path d="M5 12h14"></path>
            <path d="M12 5l7 7-7 7"></path>
          </svg>
        </div>
      </section>
    );
};

export default CustomerList;
