import React, { useState, useEffect } from "react";
import axios from "axios";
import Customers from "./Customers";
import Pagination from "./Pagination";

const CustomerList = () => {
  // const customer = {
  // 	id: "",
  // 	name: "",
  // 	email: "",
  // };
  const [customers, setCustomers] = useState([]);
  const [currentCustomers, setCurrentCustomers] = useState([]);
    const [searchQuery, setSearchQuery] = useState("");
  const [loading, setLoading] = useState(false);
  const [currentPage, setCurrentPage] = useState(1);
  const [customersPerPage, setCustomersPerPage] = useState(5);


//   const filterBy = (e) => {
//     const keyword = e.target.value;

//     if (Number.isInteger(parseInt(keyword))) {
//         const results =  customers.filter((customer) => {
//             return customer.id === parseInt(e.target.value)
//         });
//         setCurrentCustomers( results);

//     } else if (keyword.includes("@")) {
//        const results = customers.filter((customer) => {
//             return customer.email.includes(keyword.toLowerCase())
//         });

//         setCurrentCustomers( results);
//     } else if (e.target.value !== "") {
//         const results = customers.filter((customer) => {
//             return customer.name.includes(e.target.value)
//         });   

//         setCurrentCustomers( results);
//     }   else {
//         getCurrentCustomers();
//     }

//     setSearchQuery(keyword)
//   };

  const changeNbPerPage = (e) => {
    setCustomersPerPage(parseInt(e.target.value));
    setCurrentPage(1);
  };


  // Get current Customers
  const getCurrentCustomers = () => {
        const indexOfLastCustomer = currentPage * customersPerPage;
        const indexOfFirstCustomer = indexOfLastCustomer - customersPerPage;
        setCurrentCustomers(
            customers.slice(indexOfFirstCustomer, indexOfLastCustomer)
        );
  };



  useEffect(() => {
    async function fetchData() {
      setLoading(true);
      const response = await axios.get("http://localhost:8080/customers");
      setCustomers(response.data);
      setLoading(false);
      console.log("customer = " , customers)
    }
    fetchData();
    getCurrentCustomers();
  }, []);



  // change page
  const paginate = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <section className="text-gray-600 body-font">
      <div className="p-4  mt-4 flex justify-between">
        <div className=" ">
          <label htmlFor="filter" className="leading-7 text-gray-700">
            Filter By
          </label>
          <input
            type="text"
            id="filter"
            name="filter"
            value={searchQuery}
            onChange={filterBy}
            className="w-full bg-gray-100 bg-opacity-50 rounded border border-gray-300 focus:border-indigo-500 focus:bg-white focus:ring-2 focus:ring-indigo-200 text-base outline-none text-gray-700 py-1 px-3 leading-8 transition-colors duration-200 ease-in-out"
          />
        </div>

        <div className=" ">
          <label htmlFor="search" className="leading-7 text-gray-700">
            number of customers
          </label>
          <select
            id="paginationNb"
            value={customersPerPage}
            onChange={changeNbPerPage}
            className="w-full bg-gray-100 bg-opacity-50 rounded border
							border-gray-300 focus:border-indigo-500 focus:bg-white focus:ring-2
							focus:ring-indigo-200 text-base outline-none text-gray-700 py-1 px-3
							leading-8 transition-colors duration-200 ease-in-out"
          >
            {" "}
            <option value="5">5</option>
            <option value="20">20</option>
            <option value="40">40</option>
            <option value="60">60</option>
          </select>
        </div>
      </div>
      <Customers cc={currentCustomers} ll={loading} />
      <Pagination
        customersPerPage={customersPerPage}
        totalCustomers={customers.length}
        current={currentPage}
        paginate={paginate}
      />
    </section>
  );
};

export default CustomerList;
